mapper.select:

		SELECT *
		 FROM (SELECT A.*, rownum as rn
		            FROM (SELECT num, category, id, title, content, read_cnt, comment_cnt, reg_date, board_file, show
		                    FROM grb_board b
		                    WHERE show = 1
		                      AND category IN (#{category},2)
		                    ORDER BY category DESC, num DESC
		                ) A
		       )
        WHERE rn >= #{start} AND rn <= #{end}
